import React, {useState} from "react";

function Note(props) {

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const {name, value} = event.target;
        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            }
        })
    }

    function addNote(event) {
        event.preventDefault();
        setItems((prevNotes) => {
          return [...prevNotes, note];
        });
      }


    return (
        <div className = "note">
            <form>
                <h1><input type = "text" placeholder = "Title" onChange = {handleChange}/></h1>
                <textarea type = "text" placeholder = "Add note here" onChange = {handleChange} value = {note.content}/>
                <button className = "addNote" onClick = {addNote}>Add</button>
            </form>
        </div>
    );
}

export default Note;
