import React, {useState} from "react";

function CreateNote() {

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const {name, value} = event.target;
        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            };
        })
    }

    return (
        <div className = "note">
            <form>
                <h1><input type = "text" placeholder = "Title" onChange = {handleChange}/></h1>
                <textarea placeholder = "Add a note..." onChange = {handleChange} />
                <button className = "addButton">+</button>
            </form>
        </div>
    );
}

export default CreateNote;
