import React, {useState} from "react";

function CreateNote() {

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    //This function keeps record of the input entries and chagnes the state of the Note whenever the user types or deletes something
    function handleChange(event) {
        const {name, value} = event.target;
        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            };
        });
        console.log("function enabled!");
    }

    //This function gets called when the user clicks the add button to add the note 
    function submitNote(event) {
        event.preventDefault();
    }

    return (
        <div className = "note">
            <form>
                <h1><input name = "title" type = "text" placeholder = "Title" onChange = {handleChange} value = {note.title} /></h1>
                <textarea name = "content" placeholder = "Add a note..." onChange = {handleChange} value = {note.content} />
                <button className = "addButton">+</button>
            </form>
        </div>
    );
}

export default CreateNote;
